mapper.select:
SELECT 

FROM sys_interface a

WHERE a.id = #{id}

SELECT 

FROM sys_interface a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.sys_interface_id = #{sysInterfaceId.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM sys_interface a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE sys_interface SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE sys_interface_id = #{sysInterfaceId.id}
