mapper.select:
SELECT 

FROM sys_interface_whitelist a

WHERE a.id = #{id}

SELECT 

FROM sys_interface_whitelist a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.hosts = #{hosts}


		AND a.user_id = #{user.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_interface_whitelist a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_interface_whitelist SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
